import { Box, Input, Button, Text, VStack, TextArea, Select } from "native-base"
import React from "react"

const AddList = () => {
  const [form, setForm] = React.useState({
    name: "",
    category: "",
    date: "",
    description: "",
  })

  const handleOnChange = (name, value) => {
    setForm({
      ...form,
      [name]: value,
    })
  }

  const handleOnSubmit = () => {
    console.log("hai")
  }
  return (
    <Box flex={1} ml={12} justifyContent="center">
      <Text fontSize="2xl" bold mb={5}>
        Add List
      </Text>
      <VStack width={300} space={3}>
        <Input placeholder="Name" width="100%" name="name" type="text" />
        <Select placeholder="Category" width="100%" name="category" />
        <Input placeholder="Choose Date" width="100%" name="date" />
        <TextArea placeholder="Description" width="100%" name="description" />
      </VStack>
      <Button
        width={300}
        backgroundColor="#FF5555"
        onPress={handleOnSubmit}
        mt={10}
      >
        <Text color="white" fontWeight={800}>
          Add List
        </Text>
      </Button>
    </Box>
  )
}

export default AddList
