import { Box, Image, Input, Text, VStack, Button, HStack } from "native-base"
import React from "react"
import { API } from "../../config/Api"
import AsyncStorage from "@react-native-async-storage/async-storage"

const Login = ({ navigation }) => {
  const [form, setForm] = React.useState({
    email: "",
    password: "",
  })

  const handleOnChange = (name, value) => {
    setForm({
      ...form,
      [name]: value,
    })
  }

  const handleLogin = async () => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      }

      const body = JSON.stringify(form)
      const response = await API.post("/auth/login", body, config)

      if (response) {
        await AsyncStorage.setItem("token", response.data.token)
        await AsyncStorage.setItem("user_id", response.data.user._id)
        await AsyncStorage.setItem("user_name", response.data.user.firstName)
      }
      const value = await AsyncStorage.getItem("token")
      if (value !== null) {
        navigation.navigate("MyTodo")
      }
    } catch (error) {
      console.log(error)
      alert(error.response.data.message)
    }
  }
  return (
    <Box flex={1} alignItems="center" justifyContent="center">
      <Image
        style={{ height: 250, width: 250 }}
        mb={10}
        source={require("../../../assets/images/login.png")}
        alt=""
      />
      <Box>
        <Text fontSize="2xl" bold mb={5}>
          Login
        </Text>
        <VStack space={4} mb={10}>
          <Input
            width={300}
            placeholder="Email"
            type="email"
            name="email"
            onChangeText={(value) => handleOnChange("email", value)}
          />
          <Input
            width={300}
            placeholder="Password"
            type="password"
            name="password"
            onChangeText={(value) => handleOnChange("password", value)}
          />
        </VStack>
        <Button width={300} backgroundColor="#FF5555" onPress={handleLogin}>
          <Text color="white" fontWeight={800}>
            Login
          </Text>
        </Button>
      </Box>
      <HStack space={2} mt={2}>
        <Text>New Users?</Text>
        <Text color="#FF5555" onPress={() => navigation.navigate("Register")}>
          Register
        </Text>
      </HStack>
    </Box>
  )
}

export default Login
