import React from "react"
import { NavigationContainer } from "@react-navigation/native"
import { createStackNavigator } from "@react-navigation/stack"
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs"
import { StatusBar } from "native-base"
import {
  MaterialIcons,
  FontAwesome5,
  MaterialCommunityIcons,
} from "@expo/vector-icons"
import AsyncStorage from "@react-native-async-storage/async-storage"
import { UserContext } from "./src/context/UserContext"

import AddList from "./src/components/screen/AddList"
import Home from "./src/components/screen/Home"
import Login from "./src/components/screen/Login"
import Register from "./src/components/screen/Register"
import List from "./src/components/screen/List"
import Categories from "./src/components/screen/Categories"
import Detail from "./src/components/screen/Detail"

const Stack = createStackNavigator()
const Tab = createBottomTabNavigator()

const ButtonTab = () => {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        headerShown: false,
        tabBarLabel: () => {
          return null
        },
        tabBarIcon: ({ focused }) => {
          let bgColor
          if (route.name == "List") {
            bgColor = focused ? "#FF5555" : "#D9D9D9"
            return (
              <FontAwesome5 name="clipboard-list" size={25} color={bgColor} />
            )
          } else if (route.name == "AddList") {
            bgColor = focused ? "#FF5555" : "#D9D9D9"
            return (
              <MaterialCommunityIcons
                name="clipboard-plus"
                size={25}
                color={bgColor}
              />
            )
          } else if (route.name == "Categories") {
            bgColor = focused ? "#FF5555" : "#D9D9D9"
            return <MaterialIcons name="category" size={25} color={bgColor} />
          }
        },
      })}
    >
      <Stack.Screen name="List" component={List} />
      <Stack.Screen name="AddList" component={AddList} />
      <Stack.Screen name="Categories" component={Categories} />
    </Tab.Navigator>
  )
}

const Containers = () => {
  return (
    <NavigationContainer>
      <StatusBar />
      <Stack.Navigator>
        <Stack.Screen
          name="Home"
          component={Home}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="Login"
          component={Login}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="Register"
          component={Register}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="MyTodo"
          component={ButtonTab}
          options={{
            headerShown: false,
            headerTintColor: "white",
            headerMode: "screen",
          }}
        />
        <Stack.Screen
          name="Detail"
          component={Detail}
          options={{ headerShown: true, headerMode: "screen" }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  )
}

export default Containers
