import { Box, Input, Button, Text, VStack, TextArea, Select } from "native-base"
import React from "react"
import DateTimePicker from "@react-native-community/datetimepicker"

const AddList = () => {
  const [date, setDate] = React.useState(new Date())
  const [mode, setMode] = React.useState("date")
  const [show, setShow] = React.useState(false)
  const [text, setText] = React.useState("Choose Date")

  const [form, setForm] = React.useState({
    name: "",
    category: "",
    date: "",
    description: "",
  })

  const handleOnChange = (name, value) => {
    setForm({
      ...form,
      [name]: value,
    })
  }

  const handleOnSubmit = () => {
    console.log(form)
  }
  return (
    <Box flex={1} ml={12} justifyContent="center">
      <Text fontSize="2xl" bold mb={5}>
        Add List
      </Text>
      <VStack width={300} space={3}>
        <Input
          placeholder="Name"
          width="100%"
          name="name"
          type="text"
          onChangeText={(value) => handleOnChange("name", value)}
        />
        <Select
          placeholder="Category"
          width="100%"
          name="category"
          onChangeText={(value) => handleOnChange("category", value)}
        >
          Select Category
        </Select>
        <Input
          placeholder="Choose Date"
          width="100%"
          name="date"
          onChangeText={(value) => handleOnChange("date", value)}
        />
        <TextArea
          placeholder="Description"
          width="100%"
          name="description"
          onChangeText={(value) => handleOnChange("description", value)}
        />
      </VStack>
      <Button
        width={300}
        backgroundColor="#FF5555"
        onPress={handleOnSubmit}
        mt={10}
      >
        <Text color="white" fontWeight={800}>
          Add List
        </Text>
      </Button>
    </Box>
  )
}

export default AddList
